import React from 'react'
import './Item.css'
import { Link } from 'react-router-dom'
import { useContext } from 'react';

export const Item = (props) => {
    const abc = "http://localhost:3000/Assets/"+ props.image;
    const {data} = useContext(Shop)
    return (
        <div className='item'>
            <Link to={`/product/${props._id}`}><img onClick={window.scrollTo(0,0)} src={abc} alt="" /></Link>
            <p>{props.name}</p>
            <div className="item-prices">
                <div className="item-price-new">
                    ${props.new_price}
                </div>
                <div className="item-price-old">
                    ${props.old_price}
                </div>
            </div>
        </div>
    )
}
