import React, { createContext, useState } from "react";
import all_product from '../Components/Assets/all_product'
import axios from 'axios';
import useFetch from "../Components/useFetch/useFetch";

export const ShopContext = createContext(null);




const ShopContextProvider = async (props) => {
    const { data, loading, error } = useFetch("http://127.0.0.1:8800/api/products");

    let adata = data
    const getDefaultCart = () => {
        let cart = {};
        for (let index = 0; index < data.length + 1; index++) {
            cart[index] = 0;
        }
        return cart;
    }

    const [cartItems, setCartItems] = useState(getDefaultCart());
    const addToCart = (itemId) => {
        setCartItems((prev) => ({ ...prev, [itemId]: prev[itemId] + 1 }));
        // console.log(getDefaultCart());

    }
    const removeFromCart = (itemId) => {
        setCartItems((prev) => ({ ...prev, [itemId]: prev[itemId] - 1 }))
    }

    const getTotalCartAmount = () => {
        let totalAmount = 0;
        for (const item in cartItems) {
            if (cartItems[item] > 0) {
                let itemInfo = data.find((product) => product.id === Number(item));
                totalAmount += itemInfo.new_price * cartItems[item];
            }
        }
        return totalAmount;
    }

    const getTotalCartItems = () => {
        let totalItems = 0;
        for (const item in cartItems) {
            if (cartItems[item] > 0) {
                totalItems += cartItems[item];
            }
        }
        return totalItems;
    }

    const contextValue = { data, cartItems, addToCart, removeFromCart, getTotalCartAmount, getTotalCartItems };

    return (
        <ShopContext.Provider value={contextValue}>
            {props.children}
        </ShopContext.Provider>
    )
}

export default ShopContextProvider;
