import React, { useState, useEffect } from 'react';

const ImageSlider = ({ images }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    // Set up an interval to change the image every 3 seconds (adjust as needed)
    const intervalId = setInterval(() => {
      // Update the current index to the next image or back to the first one if at the end
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000);

    // Clean up the interval on component unmount to avoid memory leaks
    return () => clearInterval(intervalId);
  }, [currentIndex, images.length]);

  return (
    <div className="image-slider">
      <img src={images[currentIndex]} alt={`Slide ${currentIndex + 1}`} />
    </div>
  );
};

// Example usage
const Eero = () => {
  const imageUrls = [
    'https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.imgacademy.com%2Fabout-img-academy&psig=AOvVaw2Ml8N_aL4Uuxg30Y3ZQi84&ust=1700725703179000&source=images&cd=vfe&opi=89978449&ved=0CBIQjRxqFwoTCOjlio6P14IDFQAAAAAdAAAAABAE',
    'https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.youtube.com%2Fwatch%3Fv%3DIVTvuOBvh7U&psig=AOvVaw2Ml8N_aL4Uuxg30Y3ZQi84&ust=1700725703179000&source=images&cd=vfe&opi=89978449&ved=0CBIQjRxqFwoTCOjlio6P14IDFQAAAAAdAAAAABAJ',
    'https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.imgacademy.com%2Fboarding-school%2Fadmissions-tuition&psig=AOvVaw2Ml8N_aL4Uuxg30Y3ZQi84&ust=1700725703179000&source=images&cd=vfe&opi=89978449&ved=0CBIQjRxqFwoTCOjlio6P14IDFQAAAAAdAAAAABAQ',
    // Add more image URLs as needed
  ];

  return <ImageSlider images={imageUrls} />;
};

export default Eero;
